import React, { useState } from "react";
import "./theme.css";

// Datos iniciales simulados
const initialUsers = [
  { id: 1, username: "usuario1", email: "usuario1@gmail.com" },
  { id: 2, username: "usuario2", email: "usuario2@gmail.com" },
];

const App = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [role, setRole] = useState("");
  const [users, setUsers] = useState(initialUsers);
  const [view, setView] = useState("dashboard");
  const [newUser, setNewUser] = useState({ username: "", email: "" });
  const [editUserId, setEditUserId] = useState(null);

  const handleLogin = (e) => {
    e.preventDefault();

    if (username === "admin" && password === "1234") {
      setRole("admin");
      setIsLoggedIn(true);
    } else if (username === "user" && password === "abcd") {
      setRole("user");
      setIsLoggedIn(true);
    } else {
      alert("Credenciales incorrectas");
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername("");
    setPassword("");
    setRole("");
    setView("dashboard");
  };

  // Funciones CRUD
  const handleAddUser = (e) => {
    e.preventDefault();
    const id = users.length ? users[users.length - 1].id + 1 : 1;
    setUsers([...users, { id, ...newUser }]);
    setNewUser({ username: "", email: "" });
  };

  const handleDeleteUser = (id) => {
    setUsers(users.filter((user) => user.id !== id));
  };

  const handleEditUser = (user) => {
    setEditUserId(user.id);
    setNewUser(user);
  };

  const handleUpdateUser = (e) => {
    e.preventDefault();
    setUsers(users.map((user) => (user.id === editUserId ? newUser : user)));
    setEditUserId(null);
    setNewUser({ username: "", email: "" });
  };

  return (
    <div className="container mt-5">
      {!isLoggedIn ? (
        <div className="row justify-content-center">
          <div className="col-md-4">
            <h2 className="text-center mb-4">Iniciar Sesión</h2>
            <form onSubmit={handleLogin}>
              <div className="mb-3">
                <label htmlFor="username" className="form-label">
                  Usuario
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">
                  Contraseña
                </label>
                <input
                  type="password"
                  className="form-control"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary w-100">
                Ingresar
              </button>
            </form>
          </div>
        </div>
      ) : role === "admin" ? (
        <div className="text-center">
          <h1>Bienvenido, Administrador</h1>

          {view === "dashboard" ? (
            <>
              <p>Aquí puedes gestionar los usuarios, configuraciones y reportes.</p>
              <div className="mt-4">
                <button className="btn btn-secondary m-2" onClick={() => setView("viewUsers")}>
                  Ver Usuarios
                </button>
                <button className="btn btn-secondary m-2" onClick={() => setView("settings")}>
                  Configuración
                </button>
                <button className="btn btn-secondary m-2" onClick={() => setView("reports")}>
                  Reportes
                </button>
              </div>
              <button className="btn btn-danger mt-3" onClick={handleLogout}>
                Cerrar Sesión
              </button>
            </>
          ) : view === "viewUsers" ? (
            <>
              <h3>Lista de Usuarios</h3>

              {/* Formulario para agregar o editar usuarios */}
              <form className="mb-4" onSubmit={editUserId ? handleUpdateUser : handleAddUser}>
                <div className="row justify-content-center">
                  <div className="col-md-3">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Nombre de usuario"
                      value={newUser.username}
                      onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
                      required
                    />
                  </div>
                  <div className="col-md-3">
                    <input
                      type="email"
                      className="form-control"
                      placeholder="Email"
                      value={newUser.email}
                      onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                      required
                    />
                  </div>
                  <div className="col-md-2">
                    <button type="submit" className="btn btn-primary w-100">
                      {editUserId ? "Actualizar" : "Agregar"}
                    </button>
                  </div>
                </div>
              </form>

              {/* Lista de usuarios */}
              <ul className="list-group">
                {users.map((user) => (
                  <li key={user.id} className="list-group-item d-flex justify-content-between align-items-center">
                    <span>{user.username} ({user.email})</span>
                    <div>
                      <button
                        className="btn btn-warning btn-sm me-2"
                        onClick={() => handleEditUser(user)}
                      >
                        Editar
                      </button>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => handleDeleteUser(user.id)}
                      >
                        Eliminar
                      </button>
                    </div>
                  </li>
                ))}
              </ul>

              <button className="btn btn-secondary mt-3" onClick={() => setView("dashboard")}>
                Volver
              </button>
            </>
          ) : view === "settings" ? (
            <>
              <h3>Configuraciones</h3>
              <p>Aquí puedes gestionar la configuración de la aplicación (placeholder).</p>
              <button className="btn btn-secondary mt-3" onClick={() => setView("dashboard")}>
                Volver
              </button>
            </>
          ) : view === "reports" ? (
            <>
              <h3>Reportes</h3>
              <p>Aquí puedes generar reportes (placeholder).</p>
              <button className="btn btn-secondary mt-3" onClick={() => setView("dashboard")}>
                Volver
              </button>
            </>
          ) : null}
        </div>
      ) : (
        <div className="text-center">
          <h1>Bienvenido, Usuario</h1>
          {view === "dashboard" ? (
            <>
              <p>Consulta tu información personal y accede a los recursos disponibles.</p>
              <div className="mt-4">
                <button className="btn btn-secondary m-2" onClick={() => setView("profile")}>
                  Ver Perfil
                </button>
                <button className="btn btn-secondary m-2" onClick={() => setView("tasks")}>
                  Mis Tareas
                </button>
                <button className="btn btn-secondary m-2" onClick={() => setView("help")}>
                  Ayuda
                </button>
              </div>
              <button className="btn btn-danger mt-3" onClick={handleLogout}>
                Cerrar Sesión
              </button>
            </>
          ) : view === "profile" ? (
            <>
              <h3>Perfil</h3>
              <div class="card mb-3">
    <div class="card-header position-relative min-vh-25 mb-7">
        <div class="bg-holder rounded-3 rounded-bottom-0"></div>

        <div class="avatar avatar-5xl avatar-profile">
            <img class="rounded-circle img-thumbnail shadow-sm" src="../perfil.jpg" width="200" alt="ABCD"/>
        </div>
    </div>
    <div class="card-body">
        <div class="row">
            <div class="col-lg-8">
                <h4 class="mb-1">
                        Shreck Buchón
                    <span data-bs-toggle="tooltip" data-bs-placement="right" title="Verified">
                        <small class="fa fa-check-circle text-primary" data-fa-transform="shrink-4 down-2"></small>
                    </span>
                </h4>
                <h5 class="fs-9 fw-normal">Trainee</h5>
                <p class="text-500">Pesquería, Nuevo León</p>
                <button class="btn btn-falcon-primary btn-sm px-3" type="button">Premios</button>
                <button class="btn btn-falcon-default btn-sm px-3 ms-2" type="button">Ver más</button>
                <div class="border-bottom border-dashed my-4 d-lg-none"></div>
            </div>
            <div class="col ps-2 ps-lg-3">
                <a class="d-flex align-items-center mb-2" href="#">
                    <span class="fas fa-user-circle fs-6 me-2 text-700" data-fa-transform="grow-2"></span>
                    <div class="flex-1">
                        <h6 class="mb-0">Logros (12)</h6>
                    </div>
                </a>
                <a class="d-flex align-items-center mb-2" href="#">
                    <img class="align-self-center me-2" src="../../assets/img/logos/g.png" alt="Generic placeholder image" width="30"/>
                    <div class="flex-1">
                        <h6 class="mb-0">Facebook</h6>
                    </div>
                </a>
                <a class="d-flex align-items-center mb-2" href="#">
                    <img class="align-self-center me-2" src="../../assets/img/logos/apple.png" alt="Generic placeholder image" width="30"/>
                    <div class="flex-1">
                        <h6 class="mb-0">Twitter (X)</h6>
                    </div>
                </a>
                <a class="d-flex align-items-center mb-2" href="#">
                    <img class="align-self-center me-2" src="../../assets/img/logos/hp.png" alt="Generic placeholder image" width="30"/>
                    <div class="flex-1">
                        <h6 class="mb-0">KIA</h6>
                    </div>
                </a>
            </div>
        </div>
    </div>
</div>
              <button className="btn btn-secondary mt-3" onClick={() => setView("dashboard")}>
                Volver
              </button>
            </>
          ) : view === "tasks" ? (
            <>
              <h3>Mis Tareas</h3>
              <p>Aquí puedes ver tus tareas asignadas (placeholder).</p>
              <button className="btn btn-secondary mt-3" onClick={() => setView("dashboard")}>
                Volver
              </button>
            </>
          ) : view === "help" ? (
            <>
              <h3>Wiki de Ayuda</h3>
              <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus lacinia odio vitae
                vestibulum vestibulum. Cras venenatis euismod malesuada.
              </p>
              <button className="btn btn-secondary mt-3" onClick={() => setView("dashboard")}>
                Volver
              </button>
            </>
          ) : null}
        </div>
      )}
    </div>
  );
};

export default App;
