import React, { useState } from "react";
import "./theme.css";

// Datos iniciales simulados
const initialUsers = [
  { id: 1, username: "usuario1", email: "usuario1@gmail.com" },
  { id: 2, username: "usuario2", email: "usuario2@gmail.com" },
];

const App = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [role, setRole] = useState("");
  const [users, setUsers] = useState(initialUsers);
  const [editUserId, setEditUserId] = useState(null);
  const [newUser, setNewUser] = useState({ username: "", email: "" });

  const handleLogin = (e) => {
    e.preventDefault();

    if (username === "admin" && password === "1234") {
      setRole("admin");
      setIsLoggedIn(true);
    } else if (username === "user" && password === "abcd") {
      setRole("user");
      setIsLoggedIn(true);
    } else {
      alert("Credenciales incorrectas");
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername("");
    setPassword("");
    setRole("");
  };

  const handleAddUser = (e) => {
    e.preventDefault();
    const id = users.length ? users[users.length - 1].id + 1 : 1;
    setUsers([...users, { id, ...newUser }]);
    setNewUser({ username: "", email: "" });
  };

  const handleDeleteUser = (id) => {
    setUsers(users.filter(user => user.id !== id));
  };

  const handleEditUser = (user) => {
    setEditUserId(user.id);
    setNewUser(user);
  };

  const handleUpdateUser = (e) => {
    e.preventDefault();
    setUsers(users.map(user => (user.id === editUserId ? newUser : user)));
    setEditUserId(null);
    setNewUser({ username: "", email: "" });
  };

  return (
    <div className="container mt-5">
      {!isLoggedIn ? (
        <div className="row justify-content-center">
          <div className="col-md-4">
            <h2 className="text-center mb-4">Iniciar Sesión</h2>
            <form onSubmit={handleLogin}>
              <div className="mb-3">
                <label htmlFor="username" className="form-label">Usuario</label>
                <input
                  type="text"
                  className="form-control"
                  id="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">Contraseña</label>
                <input
                  type="password"
                  className="form-control"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary w-100">Ingresar</button>
            </form>
          </div>
        </div>
      ) : role === "admin" ? (
        // Sección para Administradores con CRUD
        <div className="text-center">
          <h1>Bienvenido, Administrador</h1>
          <p>Aquí puedes gestionar los usuarios y configuraciones.</p>

          {/* Formulario para agregar o editar usuarios */}
          <form className="mb-4" onSubmit={editUserId ? handleUpdateUser : handleAddUser}>
            <div className="row justify-content-center">
              <div className="col-md-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Nombre de usuario"
                  value={newUser.username}
                  onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
                  required
                />
              </div>
              <div className="col-md-3">
                <input
                  type="email"
                  className="form-control"
                  placeholder="Email"
                  value={newUser.email}
                  onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                  required
                />
              </div>
              <div className="col-md-2">
                <button type="submit" className="btn btn-primary w-100">
                  {editUserId ? "Actualizar" : "Agregar"}
                </button>
              </div>
            </div>
          </form>

          {/* Lista de usuarios */}
          <h3>Lista de Usuarios</h3>
          <ul className="list-group">
            {users.map(user => (
              <li key={user.id} className="list-group-item d-flex justify-content-between align-items-center">
                <span>{user.username} ({user.email})</span>
                <div>
                  <button className="btn btn-warning btn-sm me-2" onClick={() => handleEditUser(user)}>Editar</button>
                  <button className="btn btn-danger btn-sm" onClick={() => handleDeleteUser(user.id)}>Eliminar</button>
                </div>
              </li>
            ))}
          </ul>

          <button
            className="btn btn-danger mt-3"
            onClick={handleLogout}
          >
            Cerrar Sesión
          </button>
        </div>
      ) : (
        // Sección para Usuarios
        <div className="text-center">
          <h1>Bienvenido, Usuario</h1>
          <p>Consulta tu información personal y accede a los recursos disponibles.</p>
          <div className="mt-4">
            <button className="btn btn-secondary m-2">Ver Perfil</button>
            <button className="btn btn-secondary m-2">Mis Tareas</button>
            <button className="btn btn-secondary m-2">Ayuda</button>
          </div>
          <button
            className="btn btn-danger mt-3"
            onClick={handleLogout}
          >
            Cerrar Sesión
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
