import React, { useState } from "react";
import "./styles.css";

const App = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    // Validación básica (puedes mejorarla o usar una autenticación real)
    if (username === "admin" && password === "1234") {
      setIsLoggedIn(true);
    } else {
      alert("Credenciales incorrectas");
    }
  };

  return (
    <div className={`App ${menuOpen ? "open" : ""}`}>
      {!isLoggedIn ? (
        <div className="login-form">
          <h2>Iniciar Sesión</h2>
          <form onSubmit={handleLogin}>
            <div>
              <label>Usuario:</label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Contraseña:</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit">Ingresar</button>
          </form>
        </div>
      ) : (
        <>
          <button className="burger" onClick={toggleMenu}></button>
          <div className="background"></div>
          <div className="shade"></div>
          <div className="menu">
            <nav>
              <a href="Juego.html" className="button" style={{ animationDelay: "0.2s" }}>Kia Drive Deck</a>
              <a href="tutorial.html" className="button" style={{ animationDelay: "0.3s" }}>Tutorial</a>
              <a href="tabla.html" className="button" style={{ animationDelay: "0.4s" }}>Tabla</a>
              <a href="kia.html" className="button" style={{ animationDelay: "0.5s" }}>KIA</a>
              <a href="perfil.html" className="button" style={{ animationDelay: "0.6s" }}>Perfil</a>
            </nav>
          </div>
        </>
      )}
    </div>
  );
};

export default App;
