import React, { useState } from "react";
import "./theme.css";
const App = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [role, setRole] = useState(""); // Estado para almacenar el rol del usuario

  const handleLogin = (e) => {
    e.preventDefault();

    // Simulación de validación de credenciales y asignación de rol
    if (username === "admin" && password === "1234") {
      setRole("admin");
      setIsLoggedIn(true);
    } else if (username === "user" && password === "abcd") {
      setRole("user");
      setIsLoggedIn(true);
    } else {
      alert("Credenciales incorrectas");
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername("");
    setPassword("");
    setRole("");
  };

  return (
    <div className="container mt-5">
      {!isLoggedIn ? (
        <div className="row justify-content-center">
          <div className="col-md-4">
            <h2 className="text-center mb-4">Iniciar Sesión</h2>
            <form onSubmit={handleLogin}>
              <div className="mb-3">
                <label htmlFor="username" className="form-label">Usuario</label>
                <input
                  type="text"
                  className="form-control"
                  id="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">Contraseña</label>
                <input
                  type="password"
                  className="form-control"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary w-100">Ingresar</button>
            </form>
          </div>
        </div>
      ) : role === "admin" ? (
        // Vista para Administradores
        <div className="text-center">
          <h1>Bienvenido, Administrador</h1>
          <p>Aquí puedes gestionar los usuarios y configuraciones.</p>
          <div className="mt-4">
            <button className="btn btn-secondary m-2">Ver Usuarios</button>
            <button className="btn btn-secondary m-2">Configuración</button>
            <button className="btn btn-secondary m-2">Reportes</button>
          </div>
          <button
            className="btn btn-danger mt-3"
            onClick={handleLogout}
          >
            Cerrar Sesión
          </button>
        </div>
      ) : (
        // Vista para Usuarios
        <div className="text-center">
          <h1>Bienvenido, Usuario</h1>
          <p>Consulta tu información personal y accede a los recursos disponibles.</p>
          <div className="mt-4">
            <button className="btn btn-secondary m-2">Ver Perfil</button>
            <button className="btn btn-secondary m-2">Mis Tareas</button>
            <button className="btn btn-secondary m-2">Ayuda</button>
          </div>
          <button
            className="btn btn-danger mt-3"
            onClick={handleLogout}
          >
            Cerrar Sesión
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
